mapper.select:
select

from user_year_account_detail
where primary_id = #{primaryId,jdbcType=VARCHAR}

select

from user_year_account_detail
where third_consumer_id = #{thirdConsumerId,jdbcType=VARCHAR}

select sum(account_cost)
from user_year_account_detail
where consumer_id = #{consumerId,jdbcType=VARCHAR} and annual = #{annual,jdbcType=VARCHAR}

select d.primary_id, d.third_consumer_id, d.company_id, d.consumer_id, d.annual, d.account_cost, d.account_point_cost, d.account_time, t1.type_name as account_type,t2.type_name as account_channel, d.isbill, d.billno, d.bank_name, d.bank_dept, d.teller, d.account_user, d.isreconciliations, d.notes, d.create_date, u1.username as create_user, d.update_date, u2.username as update_user,d.receiptno from user_year_account_detail as d left join type_mst as t1 on d.account_type = t1.id left join type_mst as t2 on d.account_channel = t2.id left join user_login as u1 on d.create_user = u1.user_code left join user_login as u2 on d.update_user = u2.user_code where 1=1
and d.consumer_id like concat(#{consumerId},'%')
and d.annual = #{annual}
order by ${sortby}
${order}

SELECT d.primary_id, d.third_consumer_id, d.company_id, d.consumer_id, d.annual, d.account_cost, d.account_point_cost, d.account_time, d.account_type, d.receiptno, t1.type_name as accountTypeName, d.account_channel, t2.type_name as accountChannelName, d.teller, d.account_user, d.isreconciliations, t3.type_name as isreconciliationsName, d.notes, h.name as houseName, h.address as houseAddress FROM user_year_account_detail d left join type_mst t1 on t1.id = d.account_type left join type_mst t2 on t2.id = d.account_channel left join type_mst t3 on t3.id = d.isreconciliations left join houses_info h on h.consumer_id = d.consumer_id where
substring(d.consumer_id,1,10) in (select commuity_id from commuity_info where station_id = #{companyId,jdbcType=VARCHAR})
d.consumer_id like concat(#{companyId,jdbcType=VARCHAR},'%')
and DATE_FORMAT(d.account_time,'%Y-%m-%d') >= DATE_FORMAT(#{accountTimeStart,jdbcType=TIMESTAMP},'%Y-%m-%d') and DATE_FORMAT(d.account_time,'%Y-%m-%d') <= DATE_FORMAT(#{accountTimeEnd,jdbcType=TIMESTAMP},'%Y-%m-%d') and d.annual = #{currentAnnual,jdbcType=VARCHAR} and d.isreconciliations = #{isreconciliations,jdbcType=VARCHAR} and d.account_type = #{accountType,jdbcType=VARCHAR} and d.account_channel = #{accountChannel,jdbcType=VARCHAR}
order by d.${sortby}
${order}

select
t1.min_account_time,t1.max_account_time,
CONCAT(DATE_FORMAT(t1.min_account_time,'%Y/%m/%d'),' - ',DATE_FORMAT(t1.max_account_time,'%Y/%m/%d')) as account_time_range,
h.address,
type_mst.type_name as pay_state,
(uyh.adv_heat_cost + other_charge.other_total) as adv_heat_cost,
uyh.sum_account,
((uyh.adv_heat_cost + other_charge.other_total) - uyh.sum_account) as owe_account,
uyh.sum_account*100/(uyh.adv_heat_cost + other_charge.other_total) as account_rate,
uyh.sum_receivable,
uyh.margin_now,
uyh.consumer_id as id,
t_bill.bill_times,
t_bill.bill_account,
t_not_bill.not_bill_times,
t_not_bill.not_bill_account,
t1.account_times
from user_year_heat uyh
left join houses_info h on h.consumer_id=uyh.consumer_id
left join (select count(0) as account_times,a.consumer_id,min(a.account_time) as min_account_time,max(a.account_time) as max_account_time from user_year_account_detail a where
1=1

    and DATE_FORMAT(a.account_time,'%Y-%m-%d-%T') >= DATE_FORMAT(#{dateStart,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')


    and DATE_FORMAT(a.account_time,'%Y-%m-%d-%T') <= DATE_FORMAT(#{dateEnd,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')

and a.annual = #{annual}

    and company_id =#{companyId}


    and substring(consumer_id,1,10) in (
    select commuity_info.commuity_id from commuity_info where commuity_info.station_id=#{stationId}
    )


    and substring(consumer_id,1,10)=#{commuityId}


    and substring(consumer_id,1,13)=#{buildingNo}


    and substring(consumer_id,1,15)=#{unitId}


    and consumer_id =#{consumerId}

group by a.consumer_id) as t1
on t1.consumer_id = uyh.consumer_id
left join (select count(0) as bill_times,a.consumer_id,sum(a.account_cost) as bill_account from user_year_account_detail a where
1=1

    and DATE_FORMAT(a.account_time,'%Y-%m-%d-%T') >= DATE_FORMAT(#{dateStart,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')


    and DATE_FORMAT(a.account_time,'%Y-%m-%d-%T') <= DATE_FORMAT(#{dateEnd,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')

and billno is not null and billno <> '' and a.annual = #{annual}

    and company_id =#{companyId}


    and substring(consumer_id,1,10) in (
    select commuity_info.commuity_id from commuity_info where commuity_info.station_id=#{stationId}
    )


    and substring(consumer_id,1,10)=#{commuityId}


    and substring(consumer_id,1,13)=#{buildingNo}


    and substring(consumer_id,1,15)=#{unitId}


    and consumer_id =#{consumerId}

group by a.consumer_id) as t_bill
on t_bill.consumer_id = uyh.consumer_id
left join (select count(0) as not_bill_times,a.consumer_id,sum(a.account_cost) as not_bill_account from user_year_account_detail a where
1=1

    and DATE_FORMAT(a.account_time,'%Y-%m-%d-%T') >= DATE_FORMAT(#{dateStart,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')


    and DATE_FORMAT(a.account_time,'%Y-%m-%d-%T') <= DATE_FORMAT(#{dateEnd,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')

and (billno is null or billno = '') and a.annual = #{annual}

    and company_id =#{companyId}


    and substring(consumer_id,1,10) in (
    select commuity_info.commuity_id from commuity_info where commuity_info.station_id=#{stationId}
    )


    and substring(consumer_id,1,10)=#{commuityId}


    and substring(consumer_id,1,13)=#{buildingNo}


    and substring(consumer_id,1,15)=#{unitId}


    and consumer_id =#{consumerId}

group by a.consumer_id) as t_not_bill
on t_not_bill.consumer_id = uyh.consumer_id
left join (select consumer_id,sum(total) as other_total from user_year_receivable_detail where annual = #{annual,jdbcType=VARCHAR} and (begin_data_flag is null or begin_data_flag <> 'begin_data_flag_1' or charging_item =
'charging_item_3' or charging_item = 'charging_item_7' )

    and company_id =#{companyId}


    and substring(consumer_id,1,10) in (
    select commuity_info.commuity_id from commuity_info where commuity_info.station_id=#{stationId}
    )


    and substring(consumer_id,1,10)=#{commuityId}


    and substring(consumer_id,1,13)=#{buildingNo}


    and substring(consumer_id,1,15)=#{unitId}


    and consumer_id =#{consumerId}

group by user_year_receivable_detail.consumer_id) as other_charge
on other_charge.consumer_id = uyh.consumer_id
LEFT JOIN type_mst on type_mst.id = uyh.pay_over
where 1=1
and uyh.heating_status = 'heating_status_1'
and uyh.annual = #{annual}

    and DATE_FORMAT(t1.min_account_time,'%Y-%m-%d-%T') >= DATE_FORMAT(#{dateStart,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')


    and DATE_FORMAT(t1.max_account_time,'%Y-%m-%d-%T') <= DATE_FORMAT(#{dateEnd,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')


    and uyh.company_id =#{companyId}


    and substring(uyh.consumer_id,1,10) in (
    select commuity_info.commuity_id from commuity_info where commuity_info.station_id=#{stationId}
    )


    and substring(uyh.consumer_id,1,10)=#{commuityId}


    and substring(uyh.consumer_id,1,13)=#{buildingNo}


    and substring(uyh.consumer_id,1,15)=#{unitId}


    and uyh.consumer_id =#{consumerId}


    order by ${sortby}


    ${order}

select
min_account_time,
max_account_time,
CONCAT(DATE_FORMAT(min_account_time,'%Y/%m/%d'),' - ',DATE_FORMAT(max_account_time,'%Y/%m/%d')) as account_time_range,
h.commuity_name as address,
t00.adv_heat_cost as adv_heat_cost,
t00.sum_account as sum_account,
t00.owe_account as owe_account,
sum_account*100/adv_heat_cost as account_rate,
sum_receivable as sum_receivable,
margin_now as margin_now,

bill_times as bill_times,
bill_account as bill_account,
not_bill_times as not_bill_times,
not_bill_account as not_bill_account,
account_times as account_times
from
(
select
min(min_account_time) as min_account_time,
max(max_account_time) as max_account_time,
substring(t0.consumer_id,1,10) as commuity_id,
CONCAT(DATE_FORMAT(min(min_account_time),'%Y/%m/%d'),' - ',DATE_FORMAT(max(max_account_time),'%Y/%m/%d')) as account_time_range,

sum(adv_heat_cost) as adv_heat_cost,
sum(sum_account) as sum_account,
sum(owe_account) as owe_account,
sum(sum_account)*100/sum(adv_heat_cost) as account_rate,
sum(sum_receivable) as sum_receivable,
sum(margin_now) as margin_now,

sum(bill_times) as bill_times,
sum(bill_account) as bill_account,
sum(not_bill_times) as not_bill_times,
sum(not_bill_account) as not_bill_account,
sum(account_times) as account_times
from(
select
t1.min_account_time,t1.max_account_time,
CONCAT(DATE_FORMAT(t1.min_account_time,'%Y/%m/%d'),' - ',DATE_FORMAT(t1.max_account_time,'%Y/%m/%d')) as account_time_range,
h.address,
(uyh.adv_heat_cost + other_charge.other_total) as adv_heat_cost,
uyh.sum_account,
((uyh.adv_heat_cost + other_charge.other_total) - uyh.sum_account) as owe_account,
uyh.sum_account*100/(uyh.adv_heat_cost + other_charge.other_total) as account_rate,
uyh.sum_receivable,
uyh.margin_now,
uyh.consumer_id,
t_bill.bill_times,
t_bill.bill_account,
t_not_bill.not_bill_times,
t_not_bill.not_bill_account,
t1.account_times
from user_year_heat uyh
left join houses_info h on h.consumer_id=uyh.consumer_id
left join (select count(0) as account_times,a.consumer_id,min(a.account_time) as min_account_time,max(a.account_time) as max_account_time from user_year_account_detail a where
1=1

    and DATE_FORMAT(a.account_time,'%Y-%m-%d-%T') >= DATE_FORMAT(#{dateStart,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')


    and DATE_FORMAT(a.account_time,'%Y-%m-%d-%T') <= DATE_FORMAT(#{dateEnd,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')

and a.annual = #{annual}

    and company_id =#{companyId}


    and substring(consumer_id,1,10) in (
    select commuity_info.commuity_id from commuity_info where commuity_info.station_id=#{stationId}
    )


    and substring(consumer_id,1,10)=#{commuityId}


    and substring(consumer_id,1,13)=#{buildingNo}


    and substring(consumer_id,1,15)=#{unitId}


    and consumer_id =#{consumerId}

group by a.consumer_id) as t1
on t1.consumer_id = uyh.consumer_id
left join (select count(0) as bill_times,a.consumer_id,sum(a.account_cost) as bill_account from user_year_account_detail a where
1=1

    and DATE_FORMAT(a.account_time,'%Y-%m-%d-%T') >= DATE_FORMAT(#{dateStart,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')


    and DATE_FORMAT(a.account_time,'%Y-%m-%d-%T') <= DATE_FORMAT(#{dateEnd,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')

and billno is not null and billno <> '' and a.annual = #{annual}

    and company_id =#{companyId}


    and substring(consumer_id,1,10) in (
    select commuity_info.commuity_id from commuity_info where commuity_info.station_id=#{stationId}
    )


    and substring(consumer_id,1,10)=#{commuityId}


    and substring(consumer_id,1,13)=#{buildingNo}


    and substring(consumer_id,1,15)=#{unitId}


    and consumer_id =#{consumerId}

group by a.consumer_id) as t_bill
on t_bill.consumer_id = uyh.consumer_id
left join (select count(0) as not_bill_times,a.consumer_id,sum(a.account_cost) as not_bill_account from user_year_account_detail a where
1=1

    and DATE_FORMAT(a.account_time,'%Y-%m-%d-%T') >= DATE_FORMAT(#{dateStart,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')


    and DATE_FORMAT(a.account_time,'%Y-%m-%d-%T') <= DATE_FORMAT(#{dateEnd,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')

and (billno is null or billno = '') and a.annual = #{annual}

    and company_id =#{companyId}


    and substring(consumer_id,1,10) in (
    select commuity_info.commuity_id from commuity_info where commuity_info.station_id=#{stationId}
    )


    and substring(consumer_id,1,10)=#{commuityId}


    and substring(consumer_id,1,13)=#{buildingNo}


    and substring(consumer_id,1,15)=#{unitId}


    and consumer_id =#{consumerId}

group by a.consumer_id) as t_not_bill
on t_not_bill.consumer_id = uyh.consumer_id
left join (select consumer_id,sum(total) as other_total from user_year_receivable_detail where annual = #{annual,jdbcType=VARCHAR} and (begin_data_flag is null or begin_data_flag <> 'begin_data_flag_1' or charging_item =
'charging_item_3' or charging_item = 'charging_item_7' )

    and company_id =#{companyId}


    and substring(consumer_id,1,10) in (
    select commuity_info.commuity_id from commuity_info where commuity_info.station_id=#{stationId}
    )


    and substring(consumer_id,1,10)=#{commuityId}


    and substring(consumer_id,1,13)=#{buildingNo}


    and substring(consumer_id,1,15)=#{unitId}


    and consumer_id =#{consumerId}

group by user_year_receivable_detail.consumer_id) as other_charge
on other_charge.consumer_id = uyh.consumer_id
where 1=1
and uyh.heating_status = 'heating_status_1'
and uyh.annual = #{annual}

    and DATE_FORMAT(t1.min_account_time,'%Y-%m-%d-%T') >= DATE_FORMAT(#{dateStart,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')


    and DATE_FORMAT(t1.max_account_time,'%Y-%m-%d-%T') <= DATE_FORMAT(#{dateEnd,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')


    and uyh.company_id =#{companyId}


    and substring(uyh.consumer_id,1,10) in (
    select commuity_info.commuity_id from commuity_info where commuity_info.station_id=#{stationId}
    )


    and substring(uyh.consumer_id,1,10)=#{commuityId}


    and substring(uyh.consumer_id,1,13)=#{buildingNo}


    and substring(uyh.consumer_id,1,15)=#{unitId}


    and uyh.consumer_id =#{consumerId}


    order by ${sortby}


    ${order}

)as t0
GROUP BY substring(t0.consumer_id,1,10)
) as t00
LEFT JOIN commuity_info h on h.commuity_id=t00.commuity_id

select
min_account_time,
max_account_time,
CONCAT(DATE_FORMAT(min_account_time,'%Y/%m/%d'),' - ',DATE_FORMAT(max_account_time,'%Y/%m/%d')) as account_time_range,
h.building_name as address,
t00.adv_heat_cost as adv_heat_cost,
t00.sum_account as sum_account,
t00.owe_account as owe_account,
sum_account*100/adv_heat_cost as account_rate,
sum_receivable as sum_receivable,
margin_now as margin_now,

bill_times as bill_times,
bill_account as bill_account,
not_bill_times as not_bill_times,
not_bill_account as not_bill_account,
account_times as account_times
from
(
select
min(min_account_time) as min_account_time,
max(max_account_time) as max_account_time,
substring(t0.consumer_id,1,13) as building_no,
CONCAT(DATE_FORMAT(min(min_account_time),'%Y/%m/%d'),' - ',DATE_FORMAT(max(max_account_time),'%Y/%m/%d')) as account_time_range,

sum(adv_heat_cost) as adv_heat_cost,
sum(sum_account) as sum_account,
sum(owe_account) as owe_account,
sum(sum_account)*100/sum(adv_heat_cost) as account_rate,
sum(sum_receivable) as sum_receivable,
sum(margin_now) as margin_now,

sum(bill_times) as bill_times,
sum(bill_account) as bill_account,
sum(not_bill_times) as not_bill_times,
sum(not_bill_account) as not_bill_account,
sum(account_times) as account_times
from(
select
t1.min_account_time,t1.max_account_time,
CONCAT(DATE_FORMAT(t1.min_account_time,'%Y/%m/%d'),' - ',DATE_FORMAT(t1.max_account_time,'%Y/%m/%d')) as account_time_range,
h.address,
(uyh.adv_heat_cost + other_charge.other_total) as adv_heat_cost,
uyh.sum_account,
((uyh.adv_heat_cost + other_charge.other_total) - uyh.sum_account) as owe_account,
uyh.sum_account*100/(uyh.adv_heat_cost + other_charge.other_total) as account_rate,
uyh.sum_receivable,
uyh.margin_now,
uyh.consumer_id,
t_bill.bill_times,
t_bill.bill_account,
t_not_bill.not_bill_times,
t_not_bill.not_bill_account,
t1.account_times
from user_year_heat uyh
left join houses_info h on h.consumer_id=uyh.consumer_id
left join (select count(0) as account_times,a.consumer_id,min(a.account_time) as min_account_time,max(a.account_time) as max_account_time from user_year_account_detail a where
1=1

    and DATE_FORMAT(a.account_time,'%Y-%m-%d-%T') >= DATE_FORMAT(#{dateStart,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')


    and DATE_FORMAT(a.account_time,'%Y-%m-%d-%T') <= DATE_FORMAT(#{dateEnd,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')

and a.annual = #{annual}

    and company_id =#{companyId}


    and substring(consumer_id,1,10) in (
    select commuity_info.commuity_id from commuity_info where commuity_info.station_id=#{stationId}
    )


    and substring(consumer_id,1,10)=#{commuityId}


    and substring(consumer_id,1,13)=#{buildingNo}


    and substring(consumer_id,1,15)=#{unitId}


    and consumer_id =#{consumerId}

group by a.consumer_id) as t1
on t1.consumer_id = uyh.consumer_id
left join (select count(0) as bill_times,a.consumer_id,sum(a.account_cost) as bill_account from user_year_account_detail a where
1=1

    and DATE_FORMAT(a.account_time,'%Y-%m-%d-%T') >= DATE_FORMAT(#{dateStart,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')


    and DATE_FORMAT(a.account_time,'%Y-%m-%d-%T') <= DATE_FORMAT(#{dateEnd,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')

and billno is not null and billno <> '' and a.annual = #{annual}

    and company_id =#{companyId}


    and substring(consumer_id,1,10) in (
    select commuity_info.commuity_id from commuity_info where commuity_info.station_id=#{stationId}
    )


    and substring(consumer_id,1,10)=#{commuityId}


    and substring(consumer_id,1,13)=#{buildingNo}


    and substring(consumer_id,1,15)=#{unitId}


    and consumer_id =#{consumerId}

group by a.consumer_id) as t_bill
on t_bill.consumer_id = uyh.consumer_id
left join (select count(0) as not_bill_times,a.consumer_id,sum(a.account_cost) as not_bill_account from user_year_account_detail a where
1=1

    and DATE_FORMAT(a.account_time,'%Y-%m-%d-%T') >= DATE_FORMAT(#{dateStart,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')


    and DATE_FORMAT(a.account_time,'%Y-%m-%d-%T') <= DATE_FORMAT(#{dateEnd,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')

and (billno is null or billno = '') and a.annual = #{annual}

    and company_id =#{companyId}


    and substring(consumer_id,1,10) in (
    select commuity_info.commuity_id from commuity_info where commuity_info.station_id=#{stationId}
    )


    and substring(consumer_id,1,10)=#{commuityId}


    and substring(consumer_id,1,13)=#{buildingNo}


    and substring(consumer_id,1,15)=#{unitId}


    and consumer_id =#{consumerId}

group by a.consumer_id) as t_not_bill
on t_not_bill.consumer_id = uyh.consumer_id
left join (select consumer_id,sum(total) as other_total from user_year_receivable_detail where annual = #{annual,jdbcType=VARCHAR} and (begin_data_flag is null or begin_data_flag <> 'begin_data_flag_1' or charging_item =
'charging_item_3' or charging_item = 'charging_item_7' )

    and company_id =#{companyId}


    and substring(consumer_id,1,10) in (
    select commuity_info.commuity_id from commuity_info where commuity_info.station_id=#{stationId}
    )


    and substring(consumer_id,1,10)=#{commuityId}


    and substring(consumer_id,1,13)=#{buildingNo}


    and substring(consumer_id,1,15)=#{unitId}


    and consumer_id =#{consumerId}

group by user_year_receivable_detail.consumer_id) as other_charge
on other_charge.consumer_id = uyh.consumer_id
where 1=1
and uyh.heating_status = 'heating_status_1'
and uyh.annual = #{annual}

    and DATE_FORMAT(t1.min_account_time,'%Y-%m-%d-%T') >= DATE_FORMAT(#{dateStart,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')


    and DATE_FORMAT(t1.max_account_time,'%Y-%m-%d-%T') <= DATE_FORMAT(#{dateEnd,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')


    and uyh.company_id =#{companyId}


    and substring(uyh.consumer_id,1,10) in (
    select commuity_info.commuity_id from commuity_info where commuity_info.station_id=#{stationId}
    )


    and substring(uyh.consumer_id,1,10)=#{commuityId}


    and substring(uyh.consumer_id,1,13)=#{buildingNo}


    and substring(uyh.consumer_id,1,15)=#{unitId}


    and uyh.consumer_id =#{consumerId}


    order by ${sortby}


    ${order}

)as t0
GROUP BY substring(t0.consumer_id,1,13)
) as t00
LEFT JOIN build_info h on h.building_no=t00.building_no

select
min_account_time,
max_account_time,
CONCAT(DATE_FORMAT(min_account_time,'%Y/%m/%d'),' - ',DATE_FORMAT(max_account_time,'%Y/%m/%d')) as account_time_range,
h.station_name as address,
t00.adv_heat_cost as adv_heat_cost,
t00.sum_account as sum_account,
t00.owe_account as owe_account,
sum_account*100/adv_heat_cost as account_rate,
sum_receivable as sum_receivable,
margin_now as margin_now,

bill_times as bill_times,
bill_account as bill_account,
not_bill_times as not_bill_times,
not_bill_account as not_bill_account,
account_times as account_times
from
(
select
min(min_account_time) as min_account_time,
max(max_account_time) as max_account_time,
station_id,
CONCAT(DATE_FORMAT(min(min_account_time),'%Y/%m/%d'),' - ',DATE_FORMAT(max(max_account_time),'%Y/%m/%d')) as account_time_range,

sum(adv_heat_cost) as adv_heat_cost,
sum(sum_account) as sum_account,
sum(owe_account) as owe_account,
sum(sum_account)*100/sum(adv_heat_cost) as account_rate,
sum(sum_receivable) as sum_receivable,
sum(margin_now) as margin_now,

sum(bill_times) as bill_times,
sum(bill_account) as bill_account,
sum(not_bill_times) as not_bill_times,
sum(not_bill_account) as not_bill_account,
sum(account_times) as account_times
from(
select
t1.min_account_time,t1.max_account_time,
CONCAT(DATE_FORMAT(t1.min_account_time,'%Y/%m/%d'),' - ',DATE_FORMAT(t1.max_account_time,'%Y/%m/%d')) as account_time_range,
h.address,
(uyh.adv_heat_cost + other_charge.other_total) as adv_heat_cost,
uyh.sum_account,
((uyh.adv_heat_cost + other_charge.other_total) - uyh.sum_account) as owe_account,
uyh.sum_account*100/(uyh.adv_heat_cost + other_charge.other_total) as account_rate,
uyh.sum_receivable,
uyh.margin_now,
uyh.consumer_id,
station_info.station_id,
t_bill.bill_times,
t_bill.bill_account,
t_not_bill.not_bill_times,
t_not_bill.not_bill_account,
t1.account_times
from user_year_heat uyh
left join houses_info h on h.consumer_id=uyh.consumer_id
left join (select count(0) as account_times,a.consumer_id,min(a.account_time) as min_account_time,max(a.account_time) as max_account_time from user_year_account_detail a where
1=1

    and DATE_FORMAT(a.account_time,'%Y-%m-%d-%T') >= DATE_FORMAT(#{dateStart,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')


    and DATE_FORMAT(a.account_time,'%Y-%m-%d-%T') <= DATE_FORMAT(#{dateEnd,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')

and a.annual = #{annual}

    and company_id =#{companyId}


    and substring(consumer_id,1,10) in (
    select commuity_info.commuity_id from commuity_info where commuity_info.station_id=#{stationId}
    )


    and substring(consumer_id,1,10)=#{commuityId}


    and substring(consumer_id,1,13)=#{buildingNo}


    and substring(consumer_id,1,15)=#{unitId}


    and consumer_id =#{consumerId}

group by a.consumer_id) as t1
on t1.consumer_id = uyh.consumer_id
left join (select count(0) as bill_times,a.consumer_id,sum(a.account_cost) as bill_account from user_year_account_detail a where
1=1

    and DATE_FORMAT(a.account_time,'%Y-%m-%d-%T') >= DATE_FORMAT(#{dateStart,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')


    and DATE_FORMAT(a.account_time,'%Y-%m-%d-%T') <= DATE_FORMAT(#{dateEnd,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')

and billno is not null and billno <> '' and a.annual = #{annual}

    and company_id =#{companyId}


    and substring(consumer_id,1,10) in (
    select commuity_info.commuity_id from commuity_info where commuity_info.station_id=#{stationId}
    )


    and substring(consumer_id,1,10)=#{commuityId}


    and substring(consumer_id,1,13)=#{buildingNo}


    and substring(consumer_id,1,15)=#{unitId}


    and consumer_id =#{consumerId}

group by a.consumer_id) as t_bill
on t_bill.consumer_id = uyh.consumer_id
left join (select count(0) as not_bill_times,a.consumer_id,sum(a.account_cost) as not_bill_account from user_year_account_detail a where
1=1

    and DATE_FORMAT(a.account_time,'%Y-%m-%d-%T') >= DATE_FORMAT(#{dateStart,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')


    and DATE_FORMAT(a.account_time,'%Y-%m-%d-%T') <= DATE_FORMAT(#{dateEnd,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')

and (billno is null or billno = '') and a.annual = #{annual}

    and company_id =#{companyId}


    and substring(consumer_id,1,10) in (
    select commuity_info.commuity_id from commuity_info where commuity_info.station_id=#{stationId}
    )


    and substring(consumer_id,1,10)=#{commuityId}


    and substring(consumer_id,1,13)=#{buildingNo}


    and substring(consumer_id,1,15)=#{unitId}


    and consumer_id =#{consumerId}

group by a.consumer_id) as t_not_bill
on t_not_bill.consumer_id = uyh.consumer_id
LEFT JOIN station_info on substring(uyh.consumer_id,1,10) in (
select commuity_info.commuity_id from commuity_info where commuity_info.station_id=station_info.station_id
)
left join (select consumer_id,sum(total) as other_total from user_year_receivable_detail where annual = #{annual,jdbcType=VARCHAR} and (begin_data_flag is null or begin_data_flag <> 'begin_data_flag_1' or charging_item =
'charging_item_3' or charging_item = 'charging_item_7' )

    and company_id =#{companyId}


    and substring(consumer_id,1,10) in (
    select commuity_info.commuity_id from commuity_info where commuity_info.station_id=#{stationId}
    )


    and substring(consumer_id,1,10)=#{commuityId}


    and substring(consumer_id,1,13)=#{buildingNo}


    and substring(consumer_id,1,15)=#{unitId}


    and consumer_id =#{consumerId}

group by user_year_receivable_detail.consumer_id) as other_charge
on other_charge.consumer_id = uyh.consumer_id
where 1=1
and uyh.heating_status = 'heating_status_1'
and uyh.annual = #{annual}

    and DATE_FORMAT(t1.min_account_time,'%Y-%m-%d-%T') >= DATE_FORMAT(#{dateStart,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')


    and DATE_FORMAT(t1.max_account_time,'%Y-%m-%d-%T') <= DATE_FORMAT(#{dateEnd,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')


    and uyh.company_id =#{companyId}


    and substring(uyh.consumer_id,1,10) in (
    select commuity_info.commuity_id from commuity_info where commuity_info.station_id=#{stationId}
    )


    and substring(uyh.consumer_id,1,10)=#{commuityId}


    and substring(uyh.consumer_id,1,13)=#{buildingNo}


    and substring(uyh.consumer_id,1,15)=#{unitId}


    and uyh.consumer_id =#{consumerId}


    order by ${sortby}


    ${order}

)as t0
GROUP BY t0.station_id
) as t00
LEFT JOIN station_info h on h.station_id=t00.station_id

