and uyh.heating_status = 'heating_status_1'
and uyh.annual = #{annual}

    and DATE_FORMAT(t1.min_account_time,'%Y-%m-%d-%T') >= DATE_FORMAT(#{dateStart,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')


    and DATE_FORMAT(t1.max_account_time,'%Y-%m-%d-%T') <= DATE_FORMAT(#{dateEnd,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')


    and uyh.company_id =#{companyId}


    and substring(uyh.consumer_id,1,10) in (
    select commuity_info.commuity_id from commuity_info where commuity_info.station_id=#{stationId}
    )


    and substring(uyh.consumer_id,1,10)=#{commuityId}


    and substring(uyh.consumer_id,1,13)=#{buildingNo}


    and substring(uyh.consumer_id,1,15)=#{unitId}


    and uyh.consumer_id =#{consumerId}

)as t0
GROUP BY t0.company_id

select account_type,t3.id as account_channel,account_times_total,account_cost_total from
(
select type_mst.id,type_mst.type_name as account_type,t2.account_times_total,t2.account_cost_total from (
SELECT
count(0) as account_times_total,
u.account_channel as account_channel,
sum(IFNULL(u.account_cost, 0)) as account_cost_total
FROM
user_year_account_detail u
WHERE
u.account_channel IS NOT NULL
AND u.account_channel <> ''
and u.annual = #{annual}
and u.account_channel in ('pay_channel_1','pay_channel_2','pay_channel_3')
and DATE_FORMAT(u.account_time,'%Y-%m-%d-%T') >= DATE_FORMAT(#{dateStart,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')
and DATE_FORMAT(u.account_time,'%Y-%m-%d-%T') <= DATE_FORMAT(#{dateEnd,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')

    and u.company_id =#{companyId}


    and substring(u.consumer_id,1,10) in (
    select commuity_info.commuity_id from commuity_info where commuity_info.station_id=#{stationId}
    )


    and substring(u.consumer_id,1,10)=#{commuityId}


    and substring(u.consumer_id,1,13)=#{buildingNo}


    and substring(u.consumer_id,1,15)=#{unitId}


    and u.consumer_id =#{consumerId}

GROUP BY
u.account_channel
) as t2
right join type_mst on t2.account_channel = type_mst.id
where type_mst.type_kbn = 'pay_channel'
) as t3

select account_type,t3.id as account_channel,account_times_total,account_cost_total from
(
select type_mst.id,type_mst.type_name as account_type,t2.account_times_total,t2.account_cost_total from (
SELECT
count(0) as account_times_total,
u.account_type as account_type,
sum(IFNULL(u.account_cost, 0)) as account_cost_total
FROM
user_year_account_detail u
WHERE
u.account_type IS NOT NULL
AND u.account_type <> ''
and u.annual = #{annual}
and u.account_type in (select id from type_mst where type_kbn = 'payment_method')
and account_channel = #{accountChannel}
and DATE_FORMAT(u.account_time,'%Y-%m-%d-%T') >= DATE_FORMAT(#{dateStart,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')
and DATE_FORMAT(u.account_time,'%Y-%m-%d-%T') <= DATE_FORMAT(#{dateEnd,jdbcType=TIMESTAMP},'%Y-%m-%d-%T')

    and u.company_id =#{companyId}


    and substring(u.consumer_id,1,10) in (
    select commuity_info.commuity_id from commuity_info where commuity_info.station_id=#{stationId}
    )


    and substring(u.consumer_id,1,10)=#{commuityId}


    and substring(u.consumer_id,1,13)=#{buildingNo}


    and substring(u.consumer_id,1,15)=#{unitId}


    and u.consumer_id =#{consumerId}

GROUP BY
u.account_type
) as t2
right join type_mst on t2.account_type = type_mst.id
where type_mst.type_kbn = 'payment_method'
) as t3

select d.primary_id, d.third_consumer_id, d.company_id, d.consumer_id, d.annual, d.account_cost, d.account_point_cost, d.account_time, t1.type_name as account_type,t2.type_name as account_channel, d.isbill, d.billno, d.bank_name, d.bank_dept, d.teller, d.account_user, d.isreconciliations, d.notes, d.create_date, u1.username as create_user, d.update_date, u2.username as update_user,d.receiptno from user_year_account_detail as d left join type_mst as t1 on d.account_type = t1.id left join type_mst as t2 on d.account_channel = t2.id left join user_login as u1 on d.create_user = u1.user_code left join user_login as u2 on d.update_user = u2.user_code where 1=1
d.consumer_id like concat(#{id}, '%')
and d.annual = #{annual}
order by ${sortby}
${order}
